import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const openPopup = () => {
    setIsOpen(true);
  };

  const closePopup = () => {
    setIsOpen(false);
  };

  return (
    <div className="product-card">
      {/* Product Information */}
      <img src="image1.jpg" alt="TEKCOOL Hot Water Bag" className="product-image" />
      <h3>TEKCOOL Hot Water Bag</h3>
      <p>High quality guaranteed rubber hot bag, leak-proof, colorful, easy to clean, and keeps water hot for a long time.</p>
      <button onClick={openPopup} className="view-details-button">
        View Details
      </button>

      {/* Pop-up Modal */}
      {isOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <img src="image1.jpg" alt="TEKCOOL Hot Water Bag" className="popup-image" />
            <div className="text-container">
              <h3 className="header">TEKCOOL Hot Water Bag</h3>
              <p className="para">
                High quality guaranteed rubber hot bag, leak-proof, colorful, easy to clean, and keeps water hot for a long
                time.
              </p>
              <button
                onClick={() => window.open("https://amzn.to/3Zv8ggo", "_blank")}
                className="buy-on-amazon-button"
              >
                Buy on Amazon
              </button>
              <button onClick={closePopup} className="close-button">
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
