import { useState, useEffect } from "react";
import "./App.css";

function Product() {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    {
      name: "TEKCOOL Hot Water Bag",
      description: "High quality guaranteed rubber hot bag, leak-proof, colorful, easy to clean, and keeps water hot for a long time.",
      image: "image1.jpg",
      buyLink: "https://amzn.to/3Zv8ggo",
      expiryTime: Date.now() + 12 * 60 * 60 * 1000, // 12 hours from now
    },
    {
      name: "TEKCOOL Toothbrush Holders",
      description: "The length of the holder is 17.5cms, the breadth is 9cms and the height is 11.5cms. This stand is neither too big nor too small.",
      image: "image2.jpg",
      buyLink: "https://amzn.to/3Xexample",
      expiryTime: Date.now() + 12 * 60 * 60 * 1000, // 12 hours from now
    },
    // Add remaining products similarly...
  ];

  const calculateTimeLeft = (expiryTime) => {
    const difference = expiryTime - Date.now();
    if (difference <= 0) {
      return null; // Deal expired
    }
    const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
    const minutes = Math.floor((difference / (1000 * 60)) % 60);
    const seconds = Math.floor((difference / 1000) % 60);

    return { hours, minutes, seconds };
  };

  return (
    <div>
      <div className="product-grid">
        {products.map((product, index) => {
          const [timeLeft, setTimeLeft] = useState(
            calculateTimeLeft(product.expiryTime)
          );

          useEffect(() => {
            if (!timeLeft) return;
            const timer = setInterval(() => {
              setTimeLeft(calculateTimeLeft(product.expiryTime));
            }, 1000);

            return () => clearInterval(timer); // Cleanup interval on unmount
          }, [timeLeft]);

          return (
            <div key={index} className="product-card">
              <img
                src={product.image}
                alt={product.name}
                className="product-image"
              />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              {timeLeft ? (
                <div className="countdown">
                  Time Left: {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
                </div>
              ) : (
                <div className="expired">Expired Deal</div>
              )}
              <button
                onClick={() => setSelectedProduct(product)}
                className="view-details-button"
              >
                View Details
              </button>
            </div>
          );
        })}
      </div>

      {selectedProduct && (
        <div className="popup-overlay">
          <div className="popup-content">
            <img
              src={selectedProduct.image}
              alt={selectedProduct.name}
              className="popup-image"
            />
            <h3>{selectedProduct.name}</h3>
            <p>{selectedProduct.description}</p>
            <button
              onClick={() => window.open(selectedProduct.buyLink, "_blank")}
              className="buy-on-amazon-button"
            >
              Buy on Amazon
            </button>
            <button
              onClick={() => setSelectedProduct(null)}
              className="close-button"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Product;
