import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    {
      name: "TEKCOOL Hot Water Bag",
      description: "High quality guaranteed rubber hot bag, leak-proof, colorful, easy to clean, and keeps water hot for a long time.",
      image: "src/image1.jpg",
      buyLink: "https://amzn.to/3Zv8ggo",
    },
    {
      name: "CoolAir Fan",
      description: "Portable desk fan with 3 speed settings, USB powered, and energy-efficient design.",
      image: "image2.jpg",
      buyLink: "https://amzn.to/3Xexample",
    },
  ];

  return (
    <div>
      {products.map((product, index) => (
        <div key={index} className="product-card">
          <img src={product.image} alt={product.name} className="product-image" />
          <h3>{product.name}</h3>
          <p>{product.description}</p>
          <button onClick={() => setSelectedProduct(product)} className="view-details-button">
            View Details
          </button>
        </div>
      ))}

      {selectedProduct && (
        <div className="popup-overlay">
          <div className="popup-content">
            <img src={selectedProduct.image} alt={selectedProduct.name} className="popup-image" />
            <h3>{selectedProduct.name}</h3>
            <p>{selectedProduct.description}</p>
            <button
              onClick={() => window.open(selectedProduct.buyLink, "_blank")}
              className="buy-on-amazon-button"
            >
              Buy on Amazon
            </button>
            <button onClick={() => setSelectedProduct(null)} className="close-button">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
